<template>
  <div
    class="weui-tabbar"
    :style="{paddingBottom: paddingBottom + 'px'}"
  >
    <a
      class="weui-tabbar__item"
      :class="{active: index == tabIndex}"
      v-for="(item, index) in tabs"
      :key="index"
      @click="go(item, index)"
    >
      <img
        :src="'data:image/png;base64,' + item.img"
        class="weui-tabbar__icon"
        v-show="index != tabIndex"
      >
      <img
        :src="'data:image/png;base64,' + item.active"
        class="weui-tabbar__icon"
        v-show="index == tabIndex"
      >
      <p class="weui-tabbar__label">{{ item.text }}</p>
    </a>
  </div>
</template>
<script>

export default {
  components: {

  },
  props: {
    tabIndex: {
      type: String,
      default: "0"
    }
  },
  computed: {
    paddingBottom() {
      if (this.isWechat()) {
        if (this.isIPhoneX() && this.isFirst) {
          return 30;
        }
        return 0;
      }
    }
  },
  methods: {
    go(item, index) {
      if (this.tabIndex != index) {
        this.$router.push({ name: item.url })
      }
    }
  },
  data() {
    return {
      tabs: [
        {
          img: '',
          active: '',
          text: '首页',
          url: 'home'
        },
        {
          img: '',
          active: '',
          text: '我要买',
          url: 'auth.login'
        },
        {
          img: '',
          active: '',
          text: '我要卖',
          url: 'auth.login'
        },
        {
          img: '',
          active: '',
          text: '我的',
          url: 'auth.login'
        }
      ],
      isFirst: true
    }
  },
  mounted() {
    // if (this.isWechat()) {
    // }
  }
}
</script>

